package c2tc_day2;

public class Chardemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//assigning the single character literal
		char ch = 'a';
		System.out.println(ch);
		
		// assigning number into char
		char ch1 = 65;
		System.out.println(ch1);
		
		// assigning unicode
		char var = '\u00A7';
		System.out.println(var);
		
		
		// assigning unicode representation
		char var1 = '\u20AC';
		System.out.println(var1);
		
		//assigning ASCI Code
		
		int a = 'A';
		System.out.println(a);


	}

}
